package fiscalizacao.dsbrs.agems.apis;

import static org.junit.jupiter.api.Assertions.assertEquals;

import java.time.LocalDateTime;
import java.util.UUID;

import org.apache.tomcat.util.codec.binary.Base64;
import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;

import fiscalizacao.dsbrs.agems.apis.dominio.Formulario;
import fiscalizacao.dsbrs.agems.apis.dominio.Imagem;

public class ImagemTest {

  final String base64Image =
      "";
  private Imagem imagem;
  private Formulario formulario;
  private byte[] imageBytes;
  private LocalDateTime currentDate;

  @BeforeEach
  public void setUp() {
    formulario = new Formulario();
    imageBytes = Base64.decodeBase64(base64Image);
    currentDate = LocalDateTime.now();

    imagem =
      Imagem
        .builder()
        .id(UUID.fromString("82acc4ec-e0f0-4da5-803c-cc3123afe058"))
        .formulario(formulario)
        .imagem(imageBytes)
        .dataCriacao(currentDate)
        .build();
  }
  
  @Test
  public void testEncodeAndDecodeImagem() {
    byte[] bytes = imagem.getImagemBytes();
    assertEquals(bytes, imageBytes);
    assertEquals(imagem.getImagem(), base64Image);
  }
}
