package fiscalizacao.dsbrs.agems.apis;

import fiscalizacao.dsbrs.agems.apis.dominio.Formulario;
import fiscalizacao.dsbrs.agems.apis.dominio.Imagem;
import fiscalizacao.dsbrs.agems.apis.dominio.Questao;
import java.time.LocalDate;
import org.apache.tomcat.util.codec.binary.Base64;
import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;

public class ImagemTest {

  private Imagem imagem;
  private Formulario formulario;
  private Questao questao;
  private byte[] imageBytes;
  private LocalDate currentDate;

  @BeforeEach
  public void setUp() {
    formulario = new Formulario();
    questao = new Questao();
    String base64Image =
      "";
    imageBytes = Base64.decodeBase64(base64Image);
    currentDate = LocalDate.now();

    imagem =
      Imagem
        .builder()
        .id(1)
        .formulario(formulario)
        .questao(questao)
        .imagem(imageBytes)
        .dataCriacao(currentDate)
        .build();
  }

  @Test
  public void testNoArgsConstructor() {
    Imagem novaImagem = new Imagem();
    Assertions.assertNull(novaImagem.getImagem());
    Assertions.assertNull(novaImagem.getFormulario());
    Assertions.assertNull(novaImagem.getQuestao());
    Assertions.assertNull(novaImagem.getDataCriacao());
    Assertions.assertTrue(novaImagem.getId() == 0);
    String toStringEsperado =
      "Imagem(id=0, formulario=null, questao=null, imagem=null, dataCriacao=null)";
    Assertions.assertEquals(toStringEsperado, novaImagem.toString());
    novaImagem.setFormulario(formulario);
    novaImagem.setQuestao(questao);
    novaImagem.setId(1);
    novaImagem.setImagem(imagem.getImagem());
    novaImagem.setDataCriacao(LocalDate.now());
     Assertions.assertEquals(imagem.toString(), novaImagem.toString());
  }

  @Test
  public void testBuilderToString() {
    Imagem.ImagemBuilder builder = Imagem.builder();
    String toStringEsperado =
      "Imagem.ImagemBuilder(id=0, formulario=null, questao=null, imagem=null, dataCriacao=null)";
    Assertions.assertEquals(toStringEsperado, builder.toString());
  }

  @Test
  public void testAllArgsConstructor() {
    Imagem novaImagem = new Imagem(
      1,
      formulario,
      questao,
      imageBytes,
      currentDate
    );

    String toStringEsperado =
      "Imagem(id=1, formulario=Formulario(id=0, usuario=null, modelo=null, unidade=null, dataCriacao=null, observacao=null), questao=fiscalizacao.dsbrs.agems.apis.dominio.Questao@2f9f7dcf, , dataCriacao=2023-06-24)";
    Assertions.assertNotEquals(toStringEsperado, novaImagem.toString());
    Assertions.assertEquals(imagem.toString(), novaImagem.toString());
  }

  @Test
  public void testGetId() {
    int expectedId = 1;
    int actualId = imagem.getId();
    Assertions.assertEquals(expectedId, actualId);
  }

  @Test
  public void testGetFormulario() {
    Formulario expectedFormulario = formulario;
    Formulario actualFormulario = imagem.getFormulario();
    Assertions.assertEquals(expectedFormulario, actualFormulario);
  }

  @Test
  public void testGetQuestao() {
    Questao expectedQuestao = questao;
    Questao actualQuestao = imagem.getQuestao();
    Assertions.assertEquals(expectedQuestao, actualQuestao);
  }

  @Test
  public void testGetImagem() {
    byte[] expectedImageBytes = imageBytes;
    String actualImageBytes = imagem.getImagem();
    Assertions.assertArrayEquals(
      expectedImageBytes,
      Base64.decodeBase64(actualImageBytes)
    );
  }

  @Test
  public void testGetDataCriacao() {
    LocalDate expectedDate = currentDate;
    LocalDate actualDate = imagem.getDataCriacao();
    Assertions.assertEquals(expectedDate, actualDate);
  }

  @Test
  public void testSetDate() {
    imagem.setDate();
    LocalDate actualDate = imagem.getDataCriacao();
    Assertions.assertEquals(LocalDate.now(), actualDate);
  }

  @Test
  public void testSetImagem() {
    String base64Image =
      "";
    imagem.setImagem(base64Image);
    byte[] expectedImageBytes = Base64.decodeBase64(base64Image);
    byte[] actualImageBytes = Base64.decodeBase64(imagem.getImagem());
    Assertions.assertArrayEquals(expectedImageBytes, actualImageBytes);
  }
}
